INSERT INTO `psychobio_question` VALUES (1,1,_binary '\0','Укажите, пожалуйста, ваш гендер (пол)'),(2,2,_binary '\0','Укажите ваш возраст'),(3,3,_binary '\0','Укажите ваше образование'),(4,4,_binary '','Имеются ли у вас хронические заболевания либо проблемы со здоровьем, беспокоящие длительное время? Если имеются – выберите, какая из система вашего организма беспокоит больше всего (возможно несколько выборов)'),(5,5,_binary '\0','Как вы оцениваете качество своих социальных отношений (отношения с коллегами, друзьями, партнерами)'),(6,6,_binary '\0','Как вы оцениваете свое финансовое положение?'),(7,7,_binary '\0','В последнее время вы уделяете время своим хобби и интересам?'),(8,8,_binary '','Случались ли за последний год значимые для вас изменения в жизни (возможно несколько выборов)?'),(9,9,_binary '\0','Вы часто беспокоитесь о будущем?'),(10,10,_binary '\0','Вас беспокоят мысли, которые вы не можете выбросить из головы уже длительное время?'),(11,11,_binary '\0','Вы чувствуете свою беспомощность в жизненных ситуациях'),(12,12,_binary '\0','Вы вспоминаете о прошлом?'),(13,13,_binary '\0','Как вы оцениваете свою удовлетворенность жизнью?'),(14,14,_binary '\0','Где вы узнали о нашем приложении?');
